sources.styles/main.scss:
@import "utils";

body {
  background-color: $c-gray;
  box-sizing: border-box;
  margin: 0;
  font-family: "Inter", sans-serif;
  font-weight: 400;
  font-style: normal;
  position: relative;

}

p,
h1,
h2 {
  margin: 0;
  padding: 0;
}

.container {
  padding: 0 16px;
}

.icon {
  width: 20px;
  height: 20px;
  background-position: center;
  background-repeat: no-repeat;
  background-size: contain;

  &--menu {
    background-image: url(../images/menu_mobile.svg);
  }

  &--cross {
    background-image: url(../images/menu_cross.svg);
  }

  @include onDesktop {
    display: none;
  }
}

.nav {
  display: flex;
  justify-content: space-between;
  align-items: center;

  padding: 20px;

  @include onDesktop {
    padding: 32px;
  }

}

.logo {
  height: 24px;
  width: 128px;
  margin-right: 2px;

  @include onDesktop {
    margin-right: 4px;
  }

  &__img {
    width: 100%;
    height: 100%;
  }

  &__link {
    display: block;
  }
}

.hero {
  padding: 0 0 40px;

  &__about {
    color: #1c1a26;
    font-size: 16px;
    text-align: center;
    line-height: 150%;
    letter-spacing: -0.01em;
    padding: 32px 0;
  }

  &__moto {
    font-size: 64px;
    font-weight: 400;
    line-height: 85%;
    text-align: center;
    letter-spacing: -0.05em;
    text-transform: uppercase;
    color: #1c1a26;
  }

  &__img {
    width: 100%;
  }
}

.button {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 56px;
  border: 2px solid #f9512e;
  border-radius: 32px;

  font-weight: 500;
  font-size: 16px;
  line-height: 100%;
  color: #f9512e;
  letter-spacing: 0.02em;
  text-transform: uppercase;
  text-decoration: none;

  &::before {
    content: "";
    display: block;
    width: 20px;
    height: 20px;
    margin-right: 6px;
    background-image: url(../images/button_cookie_norm.svg);
  }

  &:hover {
    background-color: #f9512e;
    color: #fff;

    &::before {
      content: "";
      display: block;
      width: 20px;
      height: 20px;
      margin-right: 6px;
      background-image: url(../images/button_cookie_hover.svg);
    }
  }

  &:focus {
    background-color: #b3341a;
    color: #fff;
    border-color: #b3341a;

    &::before {
      content: "";
      display: block;
      width: 20px;
      height: 20px;
      margin-right: 6px;
      background-image: url(../images/button_cookie_hover.svg);
    }
  }
}

.menu {
  position: fixed;
  background-color: #eef7ff;
  height: 100vh;
  left: 0;
  right: 0;
  top: 0;
  transform: translateX(-100%);

  &:target {
    transform: translateX(0);
  }

  &__list {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
  }
}

.list {

  @include onMobile {
    display: none;
  }

  @include onDesktop {
    display: flex;
  }

  margin: 0;
  padding: 0;
  list-style-type: none;

  &--menu {
    display: grid;
    grid-template-columns: 1fr;
    justify-content: center;
    align-items: center;
    gap: 24px;
  }
  &__link {
    display: block;
    text-decoration: none;
    margin-left: 64px;
    text-align: center;
    font-size: 16px;
    line-height: 150%;
    letter-spacing: -0.01em;
    color: #1c1a26;

    &--menu {
      margin-left: 0;
    }

    &--last {
      color: #f9512e;
      height: 100%;
      display: flex;
      justify-content: center;
      align-items: center;
      letter-spacing: 0.02em;
      text-transform: uppercase;
      font-weight: 500;
      line-height: 100%;

      &::before {
        content: "";
        display: block;
        width: 18px;
        height: 18px;
        margin-right: 6px;
        background-image: url(../images/red_phone.png);
        background-repeat: no-repeat;
        background-position: center;
        background-size: cover;
      }
    }
  }
}

.page__welcome {
  margin-bottom: 64px;
}

.bake {
  &__title {
    font-size: 48px;
    line-height: 85%;
    letter-spacing: -0.05em;
    text-transform: uppercase;
    color: #1c1a26;
    font-weight: 400;

    margin-bottom: 24px;
  }

  &__text {
    font-size: 16px;
    line-height: 150%;
    letter-spacing: -0.01em;
    color: #1c1a26;
    margin-bottom: 56px;
  }

  &__item {
    margin-bottom: 56px;
  }
}

.sweets {

  &__photo {
    margin-bottom: 16px;
  }
  &__img {
    width: 100%;
    border-radius: 8px;
  }

  &__category {
    display: block;
    font-size: 16px;
    font-weight: 500;
    line-height: 100%;
    letter-spacing: 0.02em;
    text-transform: uppercase;
    color: #7f8096;
    text-decoration: none;
    margin-bottom: 8px;

    &:hover {
      color: #f9512e;
    }
  }

  &__about {
    font-size: 16px;
    line-height: 150%;
    letter-spacing: -0.01em;
    color: #1c1a26;
    margin-bottom: 24px;
  }

  &__order {
    font-weight: 500;
    font-size: 16px;
    line-height: 100%;
    letter-spacing: 0.02em;
    text-transform: uppercase;
    color: #f9512e;
    display: flex;
    width: max-content;
    text-decoration: none;
    margin-bottom: 8px;

    &:hover {
      font-weight: bold;
      text-decoration: underline;
    }

    &::before {
      content: "";
      display: inline-block;
      width: 16px;
      height: 16px;
      margin-right: 8px;
      background-image: url(../images/before_arrow.svg);
      background-position: center;
      background-repeat: no-repeat;
      background-size: contain;
    }
  }
}

.grid {

  @include onTablet {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 24px;
  }
  @include onDesktop {
    grid-template-columns: repeat(8, 1fr);
  }

  &--1-3 {
    display: block;

    @include onDesktop {
      grid-column: 1 / 4;
    }
  }

  &--4-6 {
    display: block;

    @include onDesktop {
      grid-column: 4 / 7;
    }
  }

  &--3-5 {
    display: block;
    @include onDesktop {
      grid-column: 3 / 6;
    }
  }
  &--6-8 {
    display: block;
    @include onDesktop {
      grid-column: 6 / 9;
    }
  }
}
